import React from "react";
import { useState } from "react";
import SupplierSignUp from "./SupplierSignUp";
import { useForm } from "react-hook-form";
import axios from "axios";
import { useNavigate } from "react-router-dom";


const SupplierSignIn = (props) => {
  const [message, setMessage] = useState("");
  const navigate = useNavigate();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    console.log(data);
    axios.post("https://cdp-backend.onrender.com/supplier/getSupplier" , data).then((res)=>{
      console.log(res);
      console.log(res.data[0]["_id"]);
      window.localStorage.setItem("supID", res.data[0]["_id"]);
      console.log(res.data[0]);
      if((res.data[0].filename === "") || (res.data[0]["supplierID"] === "None" && res.data[0]["authorizer"] !== "")){
        navigate("/uploadDocuments")
      } else if (res.data[0]["supplierID"] !== "None" && res.data[0]["authorizer"] !== "") {
        navigate('/supplierLandingPage');
      } else {
        navigate("/wait")
      }

    }).catch((err)=>{
        console.log(err);
        if (err.response.status === 401) {
          setMessage("Wrong Password");
        }
        if (err.response.status === 404) {
          setMessage("User not found");
        }
      });
  };

  const [signUp, setSignUp] = useState(0);
  return (
    <div>

      {signUp === 0 && (
        <div className="flex h-[80vh] w-full items-center ml-9">
          <div className="flex flex-col gap-9">
            <h1 className="font-bold text-4xl">Supplier Sign In</h1>
            <form
              className="flex flex-col w-full gap-9 "
              onSubmit={handleSubmit((data) => {
                onSubmit(data);
              })}
            >
              <div className="flex flex-col gap-4">
                <input
                  {...register("email", { required: "this is required" })}
                  type="email"
                  placeholder="Email"
                  className="w-96 border-2 py-1 px-2 rounded-md  "
                />

                <input
                  {...register("password", { required: "this is required" })}
                  type="password"
                  placeholder="Password"
                  className="w-96 border-2 py-1 px-2 rounded-md"
                />
              </div>
              <div className="flex gap-8">
                <button className="self-start bg-[#FCBD16] px-5 py-1 font-semibold rounded-md tracking-wide">
                  Login
                </button>
                <button
                  className="self-start border-2 border-black/50 px-5 py-1 font-semibold rounded-md tracking-wide"
                  onClick={() => {
                    props.setPage(0);
                  }}
                >
                  Back
                </button>
              </div>
            </form>
            <p>
              New to SupplyChain ?{" "}
              <button
                className="ml-4"
                onClick={() => {
                  setSignUp(1);
                }}
              >
                Sign Up
              </button>
            </p>
            <p>{message}</p>
          </div>
        </div>
      )}

      {signUp === 1 && (
        <SupplierSignUp
          signUp={signUp}
          setSignUp={setSignUp}
          page={props.page}
          setPage={props.setPage}
        />
      )}
    </div>
  );
};

export default SupplierSignIn;
